import { createStore } from 'vuex'

export default createStore({
  state: {
    dados: []
  },
  getters: {
  },
  mutations: {

    setDados(state, dados) {
      state.dados = dados;
    },
  },
  actions: {

    async carregarDados({ commit }) {
      // Aqui você carrega os dados do seu arquivo JSON
      try {
        const response = await fetch(' http://localhost:3000/registerGroup'); // Substitua pelo caminho real
        const data = await response.json();
        commit('setDados', data.dados);
      } catch (error) {
        console.error('Erro ao carregar os dados:', error);
      }
    },
  },
  modules: {
  }
})
